import React, { useState } from "react";
import axios from "axios";

const KeywordExtractor = () => {
  const [input, setInput] = useState("");
  const [keywords, setKeywords] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const getKeywords = async () => {
    if (!input) return;
    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/keywords", {
        input,
      });
      setKeywords(response.data.keywords);
    } catch (error) {
      console.error("Error fetching keywords:", error);
      setError("Failed to fetch keywords. Please try again.");
    }

    setLoading(false);
  };

  return (
    <div className="p-6 max-w-lg mx-auto bg-white rounded-2xl shadow-lg mt-10 border border-gray-200">
      <h2 className="text-2xl font-bold text-gray-800 mb-4 text-center">
        Keyword Extractor
      </h2>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter a brand or word..."
        className="border border-gray-300 p-3 w-full rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
      />
      <button
        onClick={getKeywords}
        className="mt-3 w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 rounded-lg transition duration-200 disabled:bg-gray-400"
        disabled={loading}
      >
        {loading ? "Loading..." : "Get Keywords"}
      </button>

      {error && <p className="text-red-500 mt-2 text-center">{error}</p>}

      {keywords.length > 0 && (
        <div className="mt-4 bg-gray-100 p-4 rounded-lg">
          <h3 className="font-semibold text-gray-700 mb-2">
            Extracted Keywords:
          </h3>
          <ul className="flex flex-wrap gap-2">
            {keywords.map((keyword, index) => (
              <li
                key={index}
                className="bg-blue-100 text-blue-700 px-3 py-1 rounded-lg text-sm"
              >
                {keyword}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default KeywordExtractor;
