import React, { useState } from "react";
import axios from "axios";

const KeywordExtractor = () => {
  const [input, setInput] = useState("");
  const [keywords, setKeywords] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const getKeywords = async () => {
    if (!input) return;
    setLoading(true);
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/keywords", {
        input,
      });
      setKeywords(response.data.keywords);
    } catch (error) {
      console.error("Error fetching keywords:", error);
      setError("Failed to fetch keywords. Please try again.");
    }

    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Keyword Extractor</h2>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter a brand or word..."
        style={styles.input}
      />
      <button
        onClick={getKeywords}
        style={{ ...styles.button, opacity: loading ? 0.7 : 1 }}
        disabled={loading}
      >
        {loading ? "Loading..." : "Get Keywords"}
      </button>

      {error && <p style={styles.error}>{error}</p>}

      {keywords.length > 0 && (
        <ul style={styles.keywordList}>
          {keywords.map((keyword, index) => (
            <li key={index} style={styles.keywordItem}>
              {keyword}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default KeywordExtractor;
